
document.addEventListener("DOMContentLoaded",function(e){var $=jQuery;const pathToServer=vkLtc.ajaxurl;const sendData={action:'ids'};const decodeUri=(url)=>{if(/%[0-9a-fA-F]{2}/.test(url)){try{return decodeURIComponent(url);}catch(error){return url;}}
return url;}
$.post(pathToServer,sendData,function(ps){if(typeof ps==='string'){try{ps=JSON.parse(ps);}catch(e){return;}}
if(!ps||typeof ps!=='object')return;if(!$.isEmptyObject(ps)){$.each(ps,function(id,ls){if(!ls||typeof ls!=='object')return;try{var redirectUrl=ls.re||'';var permalinkUrl=ls.pl||'';var targetBlank=ls.tg===1;var c=$('.post-'+id+' a').filter(function(){var href=decodeUri($(this).attr('href'));return href===decodeUri(redirectUrl)||href===decodeUri(permalinkUrl);});if(c.length){if(redirectUrl){$(c).attr('href',redirectUrl);}
$(c).attr('target',targetBlank?'_blank':'_self');if(targetBlank){if(!$(c).attr('rel')){$(c).attr('rel','noreferrer noopener');}}else{$(c).removeAttr('rel');}}}catch(e){}});}});},false);